import styled from "styled-components";
import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

import { deleteTodo, updateTodo } from "../../redux/modules/todos";

const TodoBox = styled.div`
  max-width: 1150px;
  background-color: white;
  margin: auto;
  border: 1px solid transparent;
  border-radius: 10px;
`;
const ButtonStyle = styled.button`
  font-weight: bold;
  color: #000000;
  border: 1px solid transparent;
  border-radius: 10px;
  background-color: transparent;
  width: 120px;
  height: 35px;
  cursor: pointer;
  box-shadow: 0px 0px 3px 0px #7c009b;
`;

const Todo = ({ todolist }) => {
  console.log(todolist);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const todolists = useSelector((state) => state.todos);
  return (
    <TodoBox>
      <div>
        <ButtonStyle
          onClick={() => {
            navigate(`/${todolist.id}`);
          }}
        >
          상세보기
        </ButtonStyle>
        <h2>{todolist.title}</h2>
        <span>{todolist.content}</span>
      </div>
      <ButtonStyle onClick={() => dispatch(deleteTodo(todolist.id))}>삭제하기</ButtonStyle>
      <ButtonStyle onClick={() => dispatch(updateTodo(todolist))}>{todolist.done === false ? "완료" : "취소"}</ButtonStyle>
    </TodoBox>
  );
};

export default Todo;
