import React from "react";
import styled from "styled-components";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";

import { createTodo } from "../../redux/modules/todos";

const Form = () => {
  const [todo, setTodo] = useState({
    title: "",
    content: "",
  });
  const { title, content } = todo;
  const onChangeHandler = (e) => {
    const { value, name } = e.target;
    setTodo({
      ...todo,
      [name]: value,
    });
  };

  //액션 객체 보낼 함수
  const dispatch = useDispatch();
  const onClickAddTodoListHandler = () => {
    dispatch(createTodo(todo));
  };

  return (
    <FormWrap>
      <InputWrap type="text" name="title" value={title} onChange={onChangeHandler}></InputWrap>
      <InputWrap type="text" name="content" value={content} onChange={onChangeHandler}></InputWrap>
      <ButtonWrap onClick={onClickAddTodoListHandler}>추가하기</ButtonWrap>
    </FormWrap>
  );
};

export default Form;

const FormWrap = styled.div`
  max-width: 1150px;
  margin: auto;
  color: #ffffff;
  border: 1px solid transparent;
  border-radius: 10px;
  padding: 10px;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
`;
const InputWrap = styled.input`
  border: 1px solid transparent;
  border-radius: 10px;
  width: 300px;
  height: 35px;
  background-color: transparent;
  box-shadow: 0px 0px 3px 0px #7c009b;
  margin: 10px;
  padding-left: 10px;
  &:focus {
    outline: 1px solid #7c009b;
    box-shadow: 0px 0px 3px 0px #7c009b;
  }
`;
const ButtonWrap = styled.button`
  font-weight: bold;
  color: #7c009b;
  border: 1px solid transparent;
  border-radius: 10px;
  background-color: transparent;
  width: 120px;
  height: 35px;
  margin: 10px;
  cursor: pointer;
  box-shadow: 0px 0px 3px 0px #7c009b;
  &:hover {
    color: white;
  }
`;
