import styled from "styled-components";
import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useSelector } from "react-redux";

const Detail = () => {
  const navigate = useNavigate();
  const todolists = useSelector((state) => state.todos);
  const param = useParams();

  const DetailTodoList = todolists.find((todolist) => todolist.id === parseInt(param.id));
  return (
    <div>
      <div key={DetailTodoList.id}>
        <div>
          <div>ID : {DetailTodoList.id}</div>
          <button
            onClick={() => {
              navigate(`/`);
            }}
          >
            이전으로
          </button>
          <h2>{DetailTodoList.title}</h2>
          <span>{DetailTodoList.content}</span>
        </div>
      </div>
    </div>
  );
};

export default Detail;
