// src/modules/todos.js

const CREATE_TODO = "CREATE_TODO";
const READ_TODO = "READ_TODO"; // 머지?
const UPDATE_TODO = "UPDATE_TODO";
const DELETE_TODO = "DELETE_TODO";

export const createTodo = (payload) => {
  return {
    type: CREATE_TODO,
    title: payload.title,
    content: payload.content,
  };
};
export const deleteTodo = (payload) => {
  return {
    type: DELETE_TODO,
    id: payload,
  };
};
export const updateTodo = (payload) => {
  return {
    type: UPDATE_TODO,
    payload: payload,
  };
};
export const readTodo = (payload) => {
  return {
    type: READ_TODO,
    title: payload.title,
    content: payload.content,
  };
};

// 초기 상태값
const initialState = { todolist: [] };

// 리듀서
const todos = (state = initialState, action) => {
  const { todolist } = state; // state.todolist를 자주 사용해야 하므로 줄이기 위해
  console.log(todolist); // [{}] 다들어있옹..
  switch (action.type) {
    case CREATE_TODO:
      return {
        todolist: [
          ...todolist, //state안에 있는 배열은 불변성을 지켜주기 위해... 을 해야한다!
          {
            id: Date.now(),
            title: action.title,
            content: action.content,
            done: false,
          },
        ],
      };
    case READ_TODO:
      return {
        todolist: [],
      }; // < - 얘가 초기값이랑 같은거 써둔거예용 넹
    case DELETE_TODO:
      return { todolist: todolist.filter((todolists) => todolists.id !== action.id) };
    //객체 안에 있는 todolist 값이 변화하므로! {todolist: 안에 filter함수를 써야합니다.}
    //반대로 말하면, filter은 배열을 반환합니다. 하지만 우리가 원하는 return값은 객체 입니다.! 그것을 기억하세요!
    case UPDATE_TODO:
      return {
        todolist: todolist.map((todolists) => {
          console.log(action.payload);
          console.log(todolists);
          if (todolists.id === action.id) {
            return action.payload;
            //   if (todolists.done === false) {
            //     return { ...todolists, done: true };
            //   } else {
            //     return { ...todolists, done: false };
            //   }
            // }
          }
        }), //
        //       case DELETE_FAVORITE:
        // return {
        //   ...state,
        //   items: state.items.map(item => {
        //     if (item.id == id) {
        //       return { ...item, isFavorite: false};
        //     }
        //     return item;
        //   })
        // }
        // return {
        //   ...state,
        //   items: state.items.map(item => {
        //     if (item.id == id) {
        //       return { ...item, isFavorite: false};
        //     }
      };
    //todolist 원래코드임!!!!!
    // ...todolist, //state안에 있는 배열은 불변성을 지켜주기 위해... 을 해야한다!
    // todolist: todolist.map((todolists) => {
    //   if (todolists.id === action.id) {
    //     if (todolists.done === false) {
    //       returntodolists.done = true;
    //     } else {
    //       todolists.done = false;
    //     }
    //   }
    // }),
    default:
      return state;
  }
};
// const onRemove = (id) => {
//   setList(list.filter((lists) => lists.id !== id));
// };

