import styled from "styled-components";
import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

import { deleteTodo, updateTodo } from "../../redux/modules/todos";

const TodoBox = styled.div`
  max-width: 1150px;
  background-color: white;
  margin: auto;
  border: 1px solid transparent;
  border-radius: 10px;
`;

const Todo = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const todolists = useSelector((state) => state.todos);
  console.log(todolists);
  return (
    <div>
      <TodoBox>
        <h2>working</h2>
        {todolists?.map((todolist) => {
          if (todolist.done === false) {
            return (
              <div key={todolist.id}>
                <div>
                  <button
                    onClick={() => {
                      navigate(`/detail/{todolist.id}`);
                    }}
                  >
                    상세보기
                  </button>
                  <h2>{todolist.title}</h2>
                  <span>{todolist.content}</span>
                </div>
                <button onClick={() => dispatch(deleteTodo(todolist.id))}>삭제하기</button>
                <button onClick={() => dispatch(updateTodo(todolist))}>{todolist.done === false ? "완료" : "취소"}</button>
              </div>
            );
          }
        })}
        <h2>done</h2>
        {todolists?.map((todolist) => {
          if (todolist.done === true) {
            return (
              <div key={todolist.id}>
                <div>
                  <button>상세보기</button>
                  <h2>{todolist.title}</h2>
                  <span>{todolist.content}</span>
                </div>
                <button onClick={() => dispatch(deleteTodo(todolist.id))}>삭제하기</button>
                <button onClick={() => dispatch(updateTodo(todolist))}>{todolist.done === false ? "완료" : "취소"}</button>
              </div>
            );
          }
        })}
      </TodoBox>
    </div>
  );
};

export default Todo;
