import styled from "styled-components";
import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useSelector } from "react-redux";

const Layout = styled.div`
  border-radius: 10px;
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  background: linear-gradient(45deg, rgb(97, 241, 252), rgb(251, 141, 255));
`;
const DetailBox = styled.div`
  width: 500px;
  height: 500px;
  display: flex;
`;

const Detail = () => {
  const navigate = useNavigate();
  const todolists = useSelector((state) => state.todos);
  const param = useParams();

  const DetailTodoList = todolists.find((todolist) => todolist.id === parseInt(param.id));
  return (
    <Layout>
      <div key={DetailTodoList.id}>
        <div>
          <div>ID : {DetailTodoList.id}</div>
          <button
            onClick={() => {
              navigate(`/`);
            }}
          >
            이전으로
          </button>
          <h2>{DetailTodoList.title}</h2>
          <span>{DetailTodoList.content}</span>
        </div>
      </div>
    </Layout>
  );
};

export default Detail;
