import React from "react";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";

import { createTodo } from "src/redux/modules/todos";

const Form = () => {
  const [todo, setTodo] = useState({
    // id: Date.now(),
    title: "",
    content: "",
    // done: false,
  });
  const { title, content } = todo;
  const onChangeHandler = (e) => {
    const { value, name } = e.target;
    setTodo({
      ...todo,
      [name]: value,
    });
  };

  //액션 객체 보낼 함수
  const dispatch = useDispatch();
  const onClickAddTodoListHandler = () => {
    dispatch(createTodo(todo));
  };

  // const done = useSelector((state) => state.todos.done);

  return (
    <div>
      <input type="text" name="title" value={title} onChange={onChangeHandler}></input>
      <input type="text" name="content" value={content} onChange={onChangeHandler}></input>
      <button onClick={onClickAddTodoListHandler}>추가하기</button>
    </div>
  );
};

export default Form;
