import styled from "styled-components";
import React from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import Button from "./elements/Button";
import { deleteTodo, updateTodo } from "../redux/modules/todos";

const Todo = ({ todolist }) => {
  console.log(todolist);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  return (
    <TodoBox>
      <TodoListTextWrap>
        <h2>{todolist.title}</h2>
        <span>{todolist.content}</span>
      </TodoListTextWrap>
      <TodoFirstLine>
        <Button
          size="100"
          onClick={() => {
            navigate(`/${todolist.id}`);
          }}
        >
          상세보기
        </Button>
        <Button onClick={() => dispatch(updateTodo(todolist))}>{todolist.done === false ? "완료" : "취소"}</Button>
        <Button onClick={() => dispatch(deleteTodo(todolist.id))}>삭제하기</Button>
      </TodoFirstLine>
    </TodoBox>
  );
};

export default Todo;

const TodoBox = styled.div`
  max-width: 330px;
  /* background-color: white; */
  /* background: linear-gradient(45deg, rgb(97, 241, 252), rgb(251, 141, 255)); */
  margin: 10px;
  padding: 10px;
  border: 1px solid #7c009b;
  border-radius: 10px;
  flex-direction: row;
  flex-wrap: wrap;

  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const TodoFirstLine = styled.div`
  flex-direction: row;
  flex-wrap: wrap;
  display: flex;
`;
const ButtonStyle = styled.button`
  font-weight: bold;
  margin: 10px;
  color: #7c009b;
  box-shadow: 0px 0px 3px 0px;
  border: 1px solid transparent;
  border-radius: 10px;
  background-color: transparent;
  width: 90px;
  height: 35px;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  &:hover {
    color: white;
  }
`;
const TodoListTextWrap = styled.div`
  color: #7c009b;
  padding: 20px;
`;
