// src/modules/todos.js

const CREATE_TODO = "CREATE_TODO";
const READ_TODO = "READ_TODO"; // 머지?
const UPDATE_TODO = "UPDATE_TODO";
const DELETE_TODO = "DELETE_TODO";

export const createTodo = (payload) => {
  return {
    type: CREATE_TODO,
    title: payload.title,
    content: payload.content,
  };
};
export const updateTodo = (payload) => {
  return {
    type: UPDATE_TODO,
    title: payload.title,
    content: payload.content,
  };
};
export const readTodo = (payload) => {
  return {
    type: READ_TODO,
    title: payload.title,
    content: payload.content,
  };
};

// 초기 상태값
const initialState = {
  todolist: [{ id: 0, title: "", content: "", done: false }],
};

// 리듀서
const todos = (state = initialState, action) => {
  const { todolist } = state; // state.todolist를 자주 사용해야 하므로 줄이기 위해
  switch (action.type) {
    case CREATE_TODO:
      return {
        todolist: [
          ...todolist, //state안에 있는 배열은 불변성을 지켜주기 위해... 을 해야한다!
          {
            id: Date.now(),
            title: action.title,
            content: action.content,
            done: false,
          },
        ],
      };
    case READ_TODO:
      return {
        todolist: todolist.slice(0, counters.length - 1),
      };
    case UPDATE_TODO:
      return {
        id: Date.now(),
        title: "",
        content: "",
        done: false,
      };

    case DELETE_TODO:
      return {};
    default:
      return state;
  }
};

// 모듈파일에서는 리듀서를 export default 한다.
export default todos;
