import React from "react";
import styled from "styled-components";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";

import { createTodo } from "../../redux/modules/todos";

const FormWrap = styled.div`
  max-width: 1150px;
  margin: auto;
`;
const button = styled.button`
  border-radius: 8px;
  cursor: pointer;
  height: 40px;
  width: 45%;
  margin: 5px;
  background-color: transparent;
`;

const Form = () => {
  const [todo, setTodo] = useState({
    // id: Date.now(),
    title: "",
    content: "",
    // done: false,
  });
  const { title, content } = todo;
  const onChangeHandler = (e) => {
    const { value, name } = e.target;
    setTodo({
      ...todo,
      [name]: value,
    });
  };

  //액션 객체 보낼 함수
  const dispatch = useDispatch();
  const onClickAddTodoListHandler = () => {
    dispatch(createTodo(todo));
  };

  return (
    <FormWrap>
      <div>
        <input type="text" name="title" value={title} onChange={onChangeHandler}></input>
        <input type="text" name="content" value={content} onChange={onChangeHandler}></input>
        <button onClick={onClickAddTodoListHandler}>추가하기</button>
      </div>
    </FormWrap>
  );
};

export default Form;
