import React from "react";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";

import { createTodo } from "../../redux/modules/todos";

const Form = () => {
  const [todo, setTodo] = useState({
    title: "",
    content: "",
  });

  const update = (e) => {
    e.preventDefault();
  };
  const { title, content } = todo;
  const onChangeHandler = (e) => {
    const { value } = e.target.value;
    setTodo({
      [value]: e.target.value,
    });
  };

  //액션 객체 보낼 함수
  const dispatch = useDispatch();
  const onClickAddTodoListHandler = () => {
    dispatch(createTodo(todo));
  };

  return (
    <form onSubmit={update}>
      <input type="text" value={title} onChange={onChangeHandler}></input>
      <input type="text" value={content} onChange={onChangeHandler}></input>
      <button onClick={onClickAddTodoListHandler} type="submit">
        추가하기
      </button>
    </form>
  );
};

export default Form;
