import styled from "styled-components";
import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useSelector } from "react-redux";

const Detail = () => {
  const navigate = useNavigate();
  const todolists = useSelector((state) => state.todos);
  const param = useParams();
  console.log(todolists);
  console.log(parseInt(param.id));

  const A = todolists.find((todolist) => todolist.id === parseInt(param.id));
  console.log(A);
  return (
    <div>
      {todolists.find((todolist) => {
        if (todolist.id === parseInt(param.id)) {
          return (
            <div key={todolist.id}>
              <div>
                <div>ID : {todolist.id}</div>
                <button
                  onClick={() => {
                    navigate(`/`);
                  }}
                >
                  이전으로
                </button>
                <h2>{todolist.title}</h2>
                <span>{todolist.content}</span>
              </div>
            </div>
          );
        }
      })}
    </div>
  );
};

export default Detail;
