    //반대로 말하면, filter은 배열을 반환합니다. 하지만 우리가 원하는 return값은 객체 입니다.! 그것을 기억하세요!
    case UPDATE_TODO:
      return {
        ...state,
        todolist: todolist.map((todolists) => {
          if (todolists.id === action.id) {
            if (todolists.done === false) {
              return { ...todolists, done: !todolists.done };
            }
          }
          return todolists;
        }),
      };
    //...은 안의 내용물만 딱 뽑아서 복사해준당.!!
    //map은 배열을 반환한다 아놔...

    //       case DELETE_FAVORITE:
    // return {
    //   ...state,
    //   items: state.items.map(item => {
    //     if (item.id == id) {
    //       return { ...item, isFavorite: false};
    //     }
    //   const newState2 = {
    //     ...state,
    //     users: users.map(user =>
    //         user.id === 1
    //             ? { ...user, jobTitles: [ ...user.jobTitles, 'Acccount Manager' ] }
    //             : { ...user, jobTitles: [ ...user.jobTitles ] }
    //     ),
    // };
    // Reducer

    //todolist 원래코드임!!!!!
    // ...todolist, //state안에 있는 배열은 불변성을 지켜주기 위해... 을 해야한다!
    // todolist: todolist.map((todolists) => {
    //   if (todolists.id === action.id) {
    //     if (todolists.done === false) {
    //       returntodolists.done = true;
    //     } else {
    //       todolists.done = false;
    //     }
    //   }
    // }),
    // todolist: todolist.map((todolists) => {
    //   if (todolists.id === action.id) {
    //     if (todolists.done === false) {
    //       return { ...todolist,  done: true };
    //     } else {
    //       return { ...todolist, done: false };
    //     }
    //   }
    // }),
    default:
      return state;
  }
};
