import styled from "styled-components";
import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useSelector } from "react-redux";

const Layout = styled.div`
  border-radius: 10px;
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  background: linear-gradient(45deg, rgb(97, 241, 252), rgb(251, 141, 255));
  width: 500px;
  height: 500px;
`;
const DetailBox = styled.div`
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 30px;
`;
const UnderLine = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;
const DetailButton = styled.button`
  font-weight: bold;
  color: #ffffff;
  border: 1px solid transparent;
  border-radius: 10px;
  background-color: transparent;
  width: 120px;
  height: 35px;
  cursor: pointer;
  box-shadow: 0px 0px 3px 0px #7c009b;
`;

const Detail = () => {
  const navigate = useNavigate();
  const todolists = useSelector((state) => state.todos);
  const param = useParams();

  const DetailTodoList = todolists.find((todolist) => todolist.id === parseInt(param.id));
  return (
    <Layout>
      <DetailBox key={DetailTodoList.id}>
        <UnderLine>
          <span>ID : {DetailTodoList.id}</span>
          <DetailButton
            onClick={() => {
              navigate(`/`);
            }}
          >
            이전으로
          </DetailButton>
        </UnderLine>
        <h2>{DetailTodoList.title}</h2>
        <span>{DetailTodoList.content}</span>
      </DetailBox>
    </Layout>
  );
};

export default Detail;
