import styled from "styled-components";
import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

import { deleteTodo, updateTodo } from "../../redux/modules/todos";

const TodoBox = styled.div`
  max-width: 1150px;
  background-color: white;
  margin: auto;
  border: 1px solid transparent;
  border-radius: 10px;
`;

const Todo = () => {
  const dispatch = useDispatch();
  const todolist = useSelector((state) => state.todos.todolist);
  console.log(todolist);
  return (
    <div>
      <TodoBox>
        {todolist?.map((todolists) => {
          console.log(todolists.id);
          return (
            <div key={todolists.id}>
              <div>
                <h2>{todolists.title}</h2>
                <span>{todolists.content}</span>
              </div>
              <button onClick={() => dispatch(deleteTodo(todolists.id))}>삭제하기</button>
              <button onClick={() => dispatch(updateTodo(todolists))}>{todolists.done === false ? "완료" : "취소"}</button>
              {/* onClick={() => dispatch(deleteTodo(todolists.id))} */}
            </div>
          );
        })}
      </TodoBox>
    </div>
  );
};

export default Todo;
