// src/modules/todos.js

const CREATE_TODO = "CREATE_TODO";
const READ_TODO = "READ_TODO"; // 머지?
const UPDATE_TODO = "UPDATE_TODO";
const DELETE_TODO = "DELETE_TODO";

export const createTodo = (payload) => {
  return {
    type: CREATE_TODO,
    title: payload.title,
    content: payload.content,
  };
};
export const deleteTodo = (payload) => {
  return {
    type: DELETE_TODO,
    id: payload,
  };
};
export const updateTodo = (payload) => {
  return {
    type: UPDATE_TODO,

    title: payload.title,
    content: payload.content,
  };
};
export const readTodo = (payload) => {
  return {
    type: READ_TODO,
    title: payload.title,
    content: payload.content,
  };
};

// 초기 상태값
const initialState = {
  todolist: [{ id: 0, title: "나는야 초기값", content: "이거 지우는 방법은 무엇일깤ㅋㅋ", done: false }],
};

// 리듀서
const todos = (state = initialState, action) => {
  const { todolist } = state; // state.todolist를 자주 사용해야 하므로 줄이기 위해
  switch (action.type) {
    case CREATE_TODO:
      return {
        todolist: [
          ...todolist, //state안에 있는 배열은 불변성을 지켜주기 위해... 을 해야한다!
          {
            id: Date.now(),
            title: action.title,
            content: action.content,
            done: false,
          },
        ],
      };
    case READ_TODO:
      return {
        todolist: [],
      };
    case UPDATE_TODO:
      return {
        todolist: [
          ...todolist, //state안에 있는 배열은 불변성을 지켜주기 위해... 을 해야한다!
          {
            id: action.id,
            title: action.title,
            content: action.content,
            done: false,
          },
          // ...todolist, //state안에 있는 배열은 불변성을 지켜주기 위해... 을 해야한다!
          // todolist(todolist.filter((todolists) => todolists.id !== action.id)),
        ],
      };
    case DELETE_TODO:
      return {};
    default:
      return state;
  }
};
// const onRemove = (id) => {
//   setList(list.filter((lists) => lists.id !== id));
// };

export default todos;
