import styled from "styled-components";
import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

import { deleteTodo, updateTodo } from "../../redux/modules/todos";

import Todo from "../todo/Todo";

function List() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const todolists = useSelector((state) => state.todos);
  console.log(todolists);
  return (
    <div>
      <TodoBox>
        <h2>working</h2>
        {todolists.map((todolist) =>
          todolist.done === false ? (
            <div key={todolist.id}>
              <Todo />
            </div>
          ) : null
        )}
        <h2>done</h2>
        {todolists?.map((todolist) => {
          if (todolist.done === true) {
            return (
              <div key={todolist.id}>
                <div>
                  <button>상세보기</button>
                  <h2>{todolist.title}</h2>
                  <span>{todolist.content}</span>
                </div>
                <button onClick={() => dispatch(deleteTodo(todolist.id))}>삭제하기</button>
                <button onClick={() => dispatch(updateTodo(todolist))}>{todolist.done === false ? "완료" : "취소"}</button>
              </div>
            );
          }
        })}
      </TodoBox>
    </div>
  );
}

export default List;
